pragma solidity 0.4.24;

import "./Owned.sol";

contract Exchangeable is Owned {

  event LogAddTransaction(address sender, address beneficiary,address exchange,uint amount);
  event LogViewBalance(address sender);
  event LogUpdateBalance(address sender,uint deduction);

  struct RemittanceStruct {
    address exchange;
    uint amount;
    uint timestamp;
  }

  mapping (bytes32 => bool) public hashUsed;
  mapping (bytes32 => RemittanceStruct) internal pendingWithdrawals;

  function hashHelper(address exchange, bytes32 beneficiary) public onlyOwner view returns(bytes32) {
    return keccak256(abi.encodePacked(exchange, beneficiary, address(this)));
  }

  modifier onlyIfDetailsAreCorrect(string passwordOne, address beneficiary,address exchange, uint amount)
  {
    require (bytes(passwordOne).length > 0,"Password One cannot be empty");
    require (hashUsed[keccak256(abi.encodePacked(passwordOne))] == false,"This password has already been used.");
    require (beneficiary != address(0),"Beneficiary address cannot be null");
    require (exchange != address(0),"Exchange address cannot be null");
    require (amount > 0,"Amount cannot be zero");
    _;
  }

  modifier onlyIfExchange(bytes32 passwordHash) 
  {
    require (passwordHash.length > 0,"Password hash cannot be empty");
    require (pendingWithdrawals[passwordHash].exchange == msg.sender,"Unauthorized!");
    require ((((pendingWithdrawals[passwordHash].timestamp - now) / 60 / 60 / 24 ) < 30), "30 day withdrawal expired");
    _;
  }

  function addTransaction(
    address beneficiary, 
    address exchange,
    string passwordOne,
    uint amount
  )  internal onlyIfDetailsAreCorrect(passwordOne, beneficiary, exchange,amount) onlyOwner
  {
    emit LogAddTransaction(msg.sender,beneficiary, exchange, amount);
    hashUsed[keccak256(abi.encodePacked(passwordOne))] = true;
    bytes32 passwordHash = hashHelper(exchange,keccak256(abi.encodePacked(passwordOne)));
    pendingWithdrawals[passwordHash] = RemittanceStruct(exchange,amount,now);
  }

  function viewBalance(
    bytes32 passwordHash
  )  internal onlyIfExchange(passwordHash) returns (uint)
  {
    emit LogViewBalance(msg.sender);
    return (pendingWithdrawals[passwordHash].amount);
  }

  function updateBalance(
    bytes32 passwordHash,
    uint deduction
  )  internal onlyIfExchange(passwordHash)
  {
    emit LogUpdateBalance(msg.sender,deduction);
    require (pendingWithdrawals[passwordHash].amount >= deduction,"Not enough funds");
    pendingWithdrawals[passwordHash].amount -= deduction;
  }
}
